// DIBUJAR EL LABERINTO

void drawLaberinto() {
	for (int i = 0; i < 35; i++) {

		for (int j = 0; j < 135; j++) {

			switch (LaberintoMatriz[i][j]) {
			case 1: cout << char(219); 
				break;
			case 0: cout << " "; 
				break;
			case 2: cout << "W"; 
				break;
			default: cout << " "; 
				break;
			}
		}
		cout << endl;
	}
}

// PERSONAJE

struct Personaji {
	int X = inX;
	int Y = inY;
	int altura = 4;
	int ancho = 4;

	string Person[4] = {
		" O ",
		"[|]",
		"d|b",
		"L|L",
	};
}Personaje;


//BORRAR PERSONAJE
void borrarPersonaji() {
	for (int i = 0; i < Personaje.altura; i++) {

		Console::SetCursorPosition(Personaje.X, Personaje.Y + i);
		for (int j = 0; j < Personaje.ancho; j++) {
			cout << " ";
		}
		cout << endl;
	}
}

//DIBUJAR PERSONAJE
void dibujarPersonaji() {
	for (int i = 0; i < Personaje.altura; i++) {
		Console::SetCursorPosition(Personaje.X, Personaje.Y + i);

		cout << Personaje.Person[i];
	}
}

//PARA EL INT MAIN, COMPROBACION DE BLOQUES.

int main() {
	drawLaberinto(); //DIBUJAMOS EL LABERINTO

	bool jugar = true;
	while (jugar == true) {
		dibujarPersonaji(); //DIBUJAMOS EL PERSONAJE

		if (_kbhit()) {
			char tecla = toupper(_getch());

			int EQUIS = Personaje.X;
			int EYE = Personaje.Y;

			switch (tecla) {
			case 'W': EYE--;
				break;
			case 'S': EYE++;
				break;
			case 'A': EQUIS--;
				break;
			case 'D': EQUIS++;
				break;
			}

			bool autorizacion = true;
			for (int x = 0; x < Personaje.ancho; x++) {
				for (int y = 0; y < Personaje.altura; y++) {
					if (LaberintoMatriz[EYE + y][EQUIS + x] == 1) {

						autorizacion = false;
					}
						
				}
			
			}
			if (autorizacion) {
				Personaje.X = EQUIS;
				Personaje.Y = EYE;
			}
		}

		Sleep(75);
	}


	return 0;
}