// DIBUJAR EL LABERINTO

void drawLaberinto() {
	for (int i = 0; i < 35; i++) {

		for (int j = 0; j < 135; j++) {

			switch (LaberintoMatriz[i][j]) {
			case 1: cout << char(219); 
				break;
			case 0: cout << " "; 
				break;
			case 2: cout << "W"; 
				break;
			default: cout << " "; 
				break;
			}
		}
		cout << endl;
	}
}

